CREATE DEFINER = 'root'@'localhost'
FUNCTION Func_Get_Auto_EmployeeCode ()
RETURNS varchar(20) CHARSET utf8mb4
DETERMINISTIC
READS SQL DATA
COMMENT 'Function lấy mã nhân viên tự tăng theo quy tắc Mã mới = Mã mới nhất hiện tại + 1'
BEGIN
  -- Thực hiện lấy code đơn giản theo cách tách phần số đằng sau mã ra và cộng thêm 1

  -- Lấy ra code mới nhất trong db hiện tại
  SET @latestCode = (SELECT
      EmployeeCode
    FROM employee
    ORDER BY EmployeeCode DESC LIMIT 1);
  -- Tách phần số ra
  SET @latestCodeID = (SELECT
      SUBSTRING(@latestCode, 3));
  -- Ghép vào string NV để lấy ra mã mới hoàn chỉnh
  SET @newCode = (SELECT
      CONCAT('NV', @latestCodeID + 1));
  RETURN @newCode;
END
$$

