PROCEDURE Proc_Employee_GetPaging (IN $Skip int(11), IN $Take int(11), IN $Sort varchar(100), IN $Where varchar(1000))
COMMENT 'Lấy danh sách nhân viên hiển thị ra list - Created By: NBDuong 06.07.2022'
BEGIN
  -- Tạo ra bảng tạm `tmp_employee` lưu thông tin nhân viên
  -- Nên sử dụng bảng tạm vì bảng này sẽ không chỉ chứa dữ liệu lấy ra đơn thuần mà còn phục vụ mục đích lọc, sắp xếp, query theo điều kiện,...
  -- Ngoài ra phải lấy dữ liệu theo phân trang và tổng số bản ghi nên việc sử dụng bảng tạm giúp việc truy vấn trở nên dễ dàng hơn
  CREATE TEMPORARY TABLE IF NOT EXISTS `tmp_employee` (
    EmployeeID char(36),
    EmployeeCode varchar(20),
    EmployeeName varchar(100),
    DateOfBirth date,
    Gender smallint(5),
    IdentityNumber varchar(25),
    IdentityIssuedPlace varchar(255),
    IdentityIssuedDate date,
    Email varchar(100),
    PhoneNumber varchar(50),
    PositionID char(36),
    PositionName varchar(36),
    DepartmentID char(36),
    DepartmentName varchar(36),
    TaxCode varchar(20),
    Salary decimal(18, 4),
    JoiningDate date,
    WorkStatus smallint(5)
  );

  -- Insert dữ liệu tương ứng vào bảng tạm temp_employee
  -- Lưu ý khi lấy dữ liệu trên list thì phòng ban và vị trí chỉ lấy thông tin tên phòng ban, tên vị trí nên phải join sang bảng `positions` và `department` để lấy thông tin
  INSERT INTO `tmp_employee`
    SELECT
      e.EmployeeID,
      e.EmployeeCode,
      e.EmployeeName,
      e.DateOfBirth,
      e.Gender,
      e.IdentityNumber,
      e.IdentityIssuedPlace,
      e.IdentityIssuedDate,
      e.Email,
      e.PhoneNumber,
      p.PositionID,
      p.PositionName,
      d.DepartmentID,
      d.DepartmentName,
      e.TaxCode,
      e.Salary,
      e.JoiningDate,
      e.WorkStatus
    FROM `employee` e
      INNER JOIN `department` d
        ON e.DepartmentID = d.DepartmentID
      INNER JOIN `positions` p
        ON e.PositionID = p.PositionID;

  #Phân trang
  -- Nếu như không có điều kiện query truyền vào thì gán 1=1 (điều kiện này luôn đúng - đây là 1 cách để set điều kiện query mặc định)
  IF IFNULL($Where, '') = '' THEN
    SET $Where = '1=1';
  END IF;

  IF IFNULL($Sort, '') = '' THEN
    SET $Sort = 'EmployeeCode DESC'; -- Hiện tại đang để default sắp xếp theo mã nhân viên giảm dần (có thể edit để thay đổi sắp xếp theo yêu cầu đề bài)
  END IF;
  IF $Take = -1 THEN
    SET @filterQuery = CONCAT('SELECT * FROM `tmp_employee` WHERE ', $Where, ' ORDER BY ', $Sort); -- Mặc định truyền -1 => Nếu truyền -1 sẽ lấy toàn bộ bản ghi
  ELSE
    SET @filterQuery = CONCAT('SELECT * FROM `tmp_employee` WHERE ', $Where, ' ORDER BY ', $Sort, ' LIMIT ', $Skip, ',', $Take); -- Nếu lấy dữ liệu trên 1 page có truyền skip, take vào thì sẽ lấy số lượng bản ghi tương ứng trên page
  END IF;

  -- Có thể để ý thấy trên yêu cầu đề bài sẽ không chỉ lấy ra thông tin nhân viên trên page mà còn lấy ra tổng số bản ghi trên toàn bộ các page
  -- Vậy nên có thể hiểu là dữ liệu mong muốn trả về trong store này là 2 dữ liệu: Thông tin nhân viên + Tổng số bản ghi
  -- => Sử dụng phương pháp execute query để trả về 2 thông tin trong cùng 1 store
  PREPARE stmt1 FROM @filterQuery;
  EXECUTE stmt1;
  DEALLOCATE PREPARE stmt1;

  SET @filterQuery = CONCAT('SELECT count(EmployeeID) AS TotalCount FROM `tmp_employee` WHERE ', $Where);
  PREPARE stmt1 FROM @filterQuery;
  EXECUTE stmt1;
  DEALLOCATE PREPARE stmt1;

  -- Drop temp table đi sau khi dùng xong để giải phóng dữ liệu
  DROP TEMPORARY TABLE `tmp_employee`;
END
$$

