--
-- Create procedure `Proc_GetUUIDV4`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetUUIDV4 ()
BEGIN
  -- Khai báo các biến
  -- v_max: số lượng muốn loop
  -- v_counter: biến chạy
  -- new_guid: GUID mới được generate ra
  DECLARE v_max int UNSIGNED DEFAULT 1000;
  DECLARE v_counter int UNSIGNED DEFAULT 0;
  DECLARE new_guid varchar(36);

  CREATE TEMPORARY TABLE IF NOT EXISTS temp_uuid (
    ID varchar(36)
  );
  WHILE v_counter < v_max DO
    SET new_guid = (SELECT
        MID(UUID(), 1, 36));
    INSERT INTO temp_uuid (ID)
      VALUES (new_guid);
    SET v_counter = v_counter + 1;
  END WHILE;

  SELECT
    *
  FROM temp_uuid;

  DROP TEMPORARY TABLE IF EXISTS temp_uuid;
END
$$

